
public class BitwiseOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 10;
		int b = 2;
		
		System.out.println(a & b); //And operations in bits
		System.out.println(a | b); //Or Operations in Bits
		System.out.println(a ^ b); //XOr Operations in Bits
		System.out.println(~a); //not operation
		
	}

}
