
public class FindBitwiseOutput {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=-20;
	    System.out.println(a^b);
	    System.out.println(a<<1); //Left shift bits by 1 place
	} //Shifiting acts as doubling a * 2^(times we shift a digit)
	//Right shift is a / 2* times we shift

}
