
public class IncementDecrementOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Post Incerement a++ First assign then increment
		//Pre-Increment ++a First Increment then assign
		
		int x=10;
		int y=x++;
		System.out.println(x);
		System.out.println(y);
		
		
		int a=10;
		int b=++x;
		System.out.println(a);
		System.out.println(b);

	}

}
